import './App.css';
import { useState } from 'react';
 let counter = 0;
function clicked(){
  counter = counter + 1;
}
function App() {
  const [counter, setCounter] = useState(0);

  return (
    <div className='App'>
      <button onClick={()=> setCounter((preButton) => preButton - 1)} >-</button>
        {counter}
        <button onClick={() => setCounter((nextButton) => nextButton + 1)}>+</button>
    </div>
  );
}

export default App;
